import {
    EDIT_USER,
    LIST_USER,
    GET_ID
} from '../../types';

export default(state,action) =>{
    switch(action.type){
        case GET_ID:
            return{
                ...state,
                userActually : listUsers.find( user => user.id === action.payload)
            }
        case EDIT_USER:
            return{
                ...state,
                editUser :true
            }
            case LIST_USER:
                return{
                    ...state,
                    listUsers : action.payload
            }

        default:
            return state;
    }
}
