import {
    LIST_USER,
    GET_ID,
    ADD_PAGE,
    REST_PAGE,
    ACTIVE_POST
} from '../../types';

export default(state,action) =>{
    switch(action.type){
        //Obtiene el id del usuario
        case GET_ID:
            return{
                ...state,
                userActually : state.listUsers.find( user => user.id === action.payload),
                editUser :true
            }
        //Obtener datos de la API
        case LIST_USER:
            return{
                ...state,
                listUsers : action.payload
        }
        //P치gina siguiente
        case ADD_PAGE:
            return{
                ...state,
                numberPage : state.numberPage+1
            }
        //P치gina anterior
        case REST_PAGE:
            return{
                ...state,
                numberPage : state.numberPage-1
            }
        //Cambiar Layout
        case ACTIVE_POST:
            return{
                ...state,
                layoutUsers:false,
                layoutPost: true,
             }
        default:
            return state;
    }
}
