import React, { useReducer,useEffect } from 'react';
import axios from 'axios';

import userContext from './userContext';
import userReducer from './userReducer';
import {
    LIST_USER,
    GET_ID,
    ADD_PAGE,
    REST_PAGE,
    ACTIVE_POST
} from '../../types';

const UserState = props =>{

    const initialState = {
        editUser : false,
        layoutUsers: true,
        layoutPost: false,
        listUsers : [],
        userActually : [],
        numberPage : 1
    }

    const [ state,dispatch ] =useReducer(userReducer,initialState);

    //NextPage
    const addPageFn = () =>{
        dispatch({
            type: ADD_PAGE
        })
    }
    //PrevPage
    const restPageFn = () =>{
        dispatch({
            type: REST_PAGE
        })
    }

    const getIdFn = id => {
        dispatch({
            type: GET_ID,
            payload: id
        })
    }

    useEffect(()=>{
        const getUsers = async () => {
            const url = `https://reqres.in/api/users?page=${state.numberPage}`;
            const res = await axios.get(url);
            dispatch({
                type: LIST_USER,
                payload: res.data.data
            })
        }
        getUsers();
    },[state.numberPage]);


    return(
        <userContext.Provider
            value={{
                editUser : state.editUser,
                listUsers : state.listUsers,
                userActually : state.userActually,
                numberPage : state.numberPage,
                getIdFn,
                addPageFn,
                restPageFn
            }}
        >
            {props.children}
        </userContext.Provider>
    )

}

export default UserState
