import {
    LIST_USER,
    GET_ID,
    ADD_PAGE,
    REST_PAGE,
    ACTIVE_POST,
    POSTS,
    RETURN_PANEL,
    DELETE_POST,
    GET_SECPAGE,
    ALBUM_ACTIVE,
    BUTTON_BACK,
    ID_GALLERY,
    SAVE_PHOTOS,
    STATE_PHOTOS,
    GALERY_ACTIVE,
    CANCEL_EDIT
} from '../../types';

export default(state,action) =>{
    switch(action.type){
        //Obtiene el id del usuario
        case GET_ID:
            return{
                ...state,
                userActually : state.listUsers.find( user => user.id === action.payload),
                editUser :true
            }
        //Obtener datos de la API
        case LIST_USER:
            return{
                ...state,
                listUsers : action.payload
        }
        //P치gina siguiente
        case ADD_PAGE:
            return{
                ...state,
                numberPage : state.numberPage+1
            }
        //P치gina anterior
        case REST_PAGE:
            return{
                ...state,
                numberPage : state.numberPage-1
            }
        //Cambiar Layout
        case ACTIVE_POST:
            return{
                ...state,
                layoutUsers:false,
                layoutPost: true,
                userActually : state.listUsers.find( user => user.id === action.payload),
             }
        case POSTS:
            return{
                ...state,
                listPosts : action.payload.filter( post => post.userId === state.userActually.id)
            }
        case RETURN_PANEL:
            return{
                ...state,
                layoutPost: false,
                layoutUsers: true,
            }
        case DELETE_POST:
            return{
                ...state,
                listPosts : state.listPosts.filter( post => post.id !== action.payload)
            }
        case GET_SECPAGE:
            return{
                ...state,
                listUsers2 : state.listUsers.concat(action.payload)
            }
        case ALBUM_ACTIVE:
            return{
                ...state,
                buttonAlbum : true,
                buttonBack: false,
            }
        case BUTTON_BACK:
            return{
                ...state,
                buttonAlbum :false,
                buttonBack : true,
                galeryActive : false,
            }
        case ID_GALLERY:
            return{
                ...state,
                pageId: parseInt(action.payload)
            }
        case SAVE_PHOTOS:
            return{
                ...state,
                apiPhotos : action.payload,
            }
        case STATE_PHOTOS:
            return{
                ...state,
                galeryState : state.apiPhotos.filter(photo=> photo.albumId === state.pageId),
            }
        case GALERY_ACTIVE:
            return{
                ...state,
                galeryActive : true,
            }
        case CANCEL_EDIT:
            return{
                ...state,
                editUser: false
            }
        default: 
            return state;
    }
}
