import React, { useContext,Fragment } from 'react';
import userContext from '../../context/user/userContext';

const Sidebar = () => {

    const userCont = useContext(userContext);
    const { userActually,editUser } = userCont;
    console.log(userActually);

    return ( 

            <aside>
                <h1>Venture<span>App</span></h1>
                <div className="proyectos">
                    {

                    (editUser) ?
                    <Fragment>
                    <form
                    className="formulario-nuevo-proyecto"
                    >
                        <img className="card-header" src={userActually.avatar} alt=""/>
                        <p>Name:</p>
                        <input 
                            type="text"
                            className="input-text wrapp"
                            name="nombre"
                            placeholder={userActually.first_name}
                        />
                        <p>LastName:</p>
                        <input 
                            type="text"
                            className="input-text wrapp"
                            name="nombre"
                            placeholder={userActually.last_name}
                        />
                        <p>Email:</p>
                        <input 
                            type="text"
                            className="input-text"
                            name="nombre"
                            placeholder={userActually.email}
                        /> 
                    </form>
                    <button
                    type="button"
                    className="btn btn-block btn-primario"
                     >Edit user</button>
                    </Fragment>
                    :
                    <h2>Select a user and update it here!!</h2>
                }
                </div>
            </aside>
     );
}

export default Sidebar;
