import React, { Fragment, useContext } from 'react';
import Sidebar from '../components/layout/Sidebar';
import Bar from '../components/layout/Bar';
import userContext from '../context/user/userContext';
import Cards from './layout/Cards';
import Post from './layout/Post';

const Panel = () => {

    const userCont = useContext(userContext);
    const { layoutPost,layoutUsers,listUsers,addPageFn,numberPage,restPageFn } = userCont;

    setTimeout(()=>{
        saveLoading(false);
      },3000); 


    return ( 
        <div className="contenedor-app">
            <Sidebar/>
            <div className="seccion-principal">
                <Bar />
                    {
                    (layoutUsers)?
                    <Fragment>
                    <main>
                        <div className="contenedor-tareas">
                            <div className="wrapper">
                                {
                                    listUsers.map(use => (
                                            <Cards 
                                                key={use.id}
                                                id={use.id}
                                                name={use.first_name}
                                                lastname={use.last_name}
                                                avatar={use.avatar}
                                                email={use.email}
                                            />
                                    ))
                                }
                            </div>
                        </div>
                    </main>
                    <div className="container">
                        {
                            (numberPage<2)?
                            <button
                                type="btn"
                                className="btn right btn-primario"
                                onClick={() => addPageFn()}
                            >Next Page</button>
                            :null
                        }
                        {
                            (numberPage>1) ?
                            <button
                                type="btn"
                                className="btn left btn-primario"
                                onClick={() => restPageFn()}
                            >Prev Page</button>
                            : null
                        }
                    </div>
                    </Fragment>
                    : <Post/>
                    }
            </div>
        </div>
     );
}

export default Panel;
