import {
    LIST_USER,
    GET_ID,
    ADD_PAGE,
    REST_PAGE
} from '../../types';

export default(state,action) =>{
    switch(action.type){
        //Obtiene el id del usuario
        case GET_ID:
            return{
                ...state,
                userActually : state.listUsers.find( user => user.id === action.payload),
                editUser :true
            }
        //Obtener datos de la API
        case LIST_USER:
            return{
                ...state,
                listUsers : action.payload
        }

        case ADD_PAGE:
            return{
                ...state,
                numberPage : state.numberPage+1
            }
        case REST_PAGE:
            return{
                ...state,
                numberPage : state.numberPage-1
            }

        default:
            return state;
    }
}
