import axios from 'axios';
import React, { useState,useContext, useEffect, Fragment} from 'react';
import { Redirect } from 'react-router-dom';
import userContext from '../../context/user/userContext';
import AlertaContext from '../../context/alertas/alertaContext';

const Login = () => {

    const userCont = useContext(userContext);
    const { saveTokenFn,token } = userCont;


    //Extraer de context
    const alertContext = useContext(AlertaContext);
    const { mostrarAlerta } = alertContext;

    //State
    const [ user, saveUser] = useState({
        email: '',
        password: ''
    });

    const { email,password } = user;

    const onChangePage = e =>{
        saveUser({
            ...user,
            [ e.target.name] : e.target.value
        })
    }

    const onSubmitToken = async() =>{
        if(email.trim() === '' || password.trim()===''){
            mostrarAlerta('Todos los campos son obligatorios','alerta-error');
        }
        try {
            let res = await axios.post('https://reqres.in/api/login',user);
            saveTokenFn(res.data);
            /*<Link to={'/panel/users'}>

            </Link>*/

        } catch (error) {
            console.log(error);
        }
    }


    return ( 
        <Fragment>
            {
                (token===false) ?

                <div className="form-usuario">
            <div className="contenedor-form">
                <h1>Sign In</h1>
                <form
                >
                    <div className="campo-form">
                        <label htmlFor="email">Email</label>
                        <input 
                            type="email"
                            id="email"
                            name="email"
                            placeholder="eve.holt@reqres.in"
                            value={email}
                            onChange={onChangePage}
                        />
                    </div>
                    <div className="campo-form">
                        <label htmlFor="password">Password</label>
                        <input 
                            type="password"
                            id="password"
                            name="password"
                            value={password}
                            placeholder="Your Password"
                            onChange={onChangePage}
                        />
                    </div>
                    <div className="campo-form">
                        <input
                            type="button"
                            className="btn btn-primario btn-block"
                            value="Log In"
                            onClick={()=>onSubmitToken()}
                        />

                    </div>
                </form>
            </div>
        </div>
            : <Redirect to="/panel-users/"/>
            }

        </Fragment>

     );
}

export default Login;
