import{
    API_POSTS,
    SAVE_USER
} from '../../types';

export default (state,action) =>{
    switch(action.type){

        case API_POSTS:
            return{
                ...state,
                usersPosts : action.payload
            }
        case SAVE_USER:
            return{
                ...state,
                userActive: action.payload
            }

        default:
            return state;
    }
}
