import React, { useContext } from 'react';
import userContext from '../../context/user/userContext';


const Cards = ({name,lastname,avatar,id,email}) => {

    const userCont = useContext(userContext);
    const { getIdFn,changeLayoutFn } = userCont;


    return ( 
    <div class="center">
        <div class="property-card">
            <a href="#!">
            <div class="property-image">
                <div class="property-image-title">
                    <img className="" src={avatar} alt=""/>
                </div>
            </div></a>
            <div class="property-description">
                <h2>{name} {lastname}</h2>
            <p>{email}</p>
            </div>
        </div>
        <div className="estado">
            <button
                onClick={()=>{
                    getIdFn(id);
                }}
            type="button" className="incompleto">Edit user</button>
            <button
                onClick={()=>{
                    changeLayoutFn(id)
                }}
            type="button" className="completo">Posts</button>
            <button
            type="button" className="completo">Albums</button>
            </div>
    </div>
     );
}

export default Cards;
