import React, { useReducer,useEffect } from 'react';
import axios from 'axios';

import userContext from './userContext';
import userReducer from './userReducer';
import {
    LIST_USER,
    GET_ID,
    ADD_PAGE,
    REST_PAGE,
    ACTIVE_POST,
    POSTS,
    RETURN_PANEL,
    DELETE_POST
} from '../../types';

const UserState = props =>{

    const initialState = {
        editUser : false,
        layoutUsers: true,
        layoutPost: false,
        listUsers : [],
        userActually : null,
        numberPage : 1,

        listPosts :[]
    }

    const [ state,dispatch ] =useReducer(userReducer,initialState);

    //NextPage
    const addPageFn = () =>{
        dispatch({
            type: ADD_PAGE
        })
    }
    //PrevPage
    const restPageFn = () =>{
        dispatch({
            type: REST_PAGE
        })
    }
    //User active
    const getIdFn = id => {
        dispatch({
            type: GET_ID,
            payload: id
        })
    }

    //Change Layout
    const changeLayoutFn = id =>{
        dispatch({
            type: ACTIVE_POST,
            payload:id
        })
    }

    //Return Panel
    const backPanelFn = () =>{
        dispatch({
            type : RETURN_PANEL
        })
    }

    const deletePostFn = id =>{
        console.log(id);
        dispatch({
            type:DELETE_POST,
            payload: id
        })
    }



    useEffect(()=>{
        const getUsers = async () => {
            const url = `https://reqres.in/api/users?page=${state.numberPage}`;
            const res = await axios.get(url);
            dispatch({
                type: LIST_USER,
                payload: res.data.data
            })
        }
        const getPosts = async () =>{
            const url = `https://jsonplaceholder.typicode.com/posts`;
            const res= await axios.get(url);
            dispatch({
                type: POSTS,
                payload: res.data
            });
        }
        if( state.layoutPost ){
            getPosts();
        }
        getUsers();
    },[state.numberPage,state.layoutPost]);


    return(
        <userContext.Provider
            value={{
                editUser : state.editUser,
                listUsers : state.listUsers,
                userActually : state.userActually,
                numberPage : state.numberPage,
                layoutUsers: state.layoutUsers,
                layoutPost: state.layoutPost,
                listPosts: state.listPosts,
                getIdFn,
                addPageFn,
                restPageFn,
                changeLayoutFn,
                backPanelFn,
                deletePostFn
            }}
        >
            {props.children}
        </userContext.Provider>
    )

}

export default UserState
