import React,{ Fragment, useContext } from 'react';
import userContext from '../../context/user/userContext';

const Post = () => {

    const userCont = useContext(userContext);
    const { listPosts } = userCont;

    return ( 
        <Fragment>
        {  
            listPosts.map(post =>(
                <div class="row">
                    <div class="column">
                    <div class="card">
                    <h3>{post.title}</h3>
                    <p>Some text</p>
                    </div>
                    </div>
                </div>
            ))
        }
        </Fragment>
     );
}

export default Post;
